INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('APQP', 'Advanced Product Quality Planning. Planejamento Avançado da Qualidade do Produto;');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Aprovação de Engenharia do Cliente', 'Onde especificado pelo cliente, a organização deve ter evidência da aprovação de engenharia do cliente');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Arte', 'Elaboração da Arte Final para aprovação - Padrão Brady');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('ASN', 'Advanced Shipped Notice, que é traduzido como “aviso antecipado de embarque” ou simplesmente “aviso de expedição”');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('ASQ', 'Avaliação do Sistema da Qualidade. Auditoria que avalia o Sistema da Qualidade do fornecedor;');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('ASTEC', 'Divisão de Peças Reposição / Service Parts Honda Auto e MotoHonda');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Alternative packaging (Embalagem Alternativa)', 'Uma alternativa à embalagem acordada para a entrega em série ');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV', 'Auditorias realizadas no Fornecedor');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV I', 'Auditoria periódica para verificação do sistema da qualidade dos fornecedores');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II', 'Estabelece critérios de verificação das auditorias de desenvolvimento, desde a avaliação do planejamento de desenvolvimento do fornecedor até a homologação do processo');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II.2', 'Verificação do Planejamento do Fornecedor para o Desenvolvimento, realizado após o Dandori Go. Deve ser realizado antes do início da confecção dos moldes e ferramentais');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II.3', 'Verificação do Andamento do Desenvolvimento do Fornecedor, deve ser realizado durante a confecção dos moldes e ferramentais até os primeiros Try Outs');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II.4', 'Verificação do Nível da Qualidade do Produto e Processo do Fornecedor');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II.5', 'Verificação dos Preparativos para Início da Produção em Massa');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II-M', 'Define critérios para realização da auditoria do sistema da qualidade dos fornecedores de matéria-prima e/ou materias secundários');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II-P', 'Apresenta auditoria ocasional, executada base os resultados dos indicadores de qualidade de produção');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Qualidade de Fornecedores - QAV II-S', 'Define critérios para realização da auditoria do sistema da qualidade de prestadores de serviço');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria de Vigilância', 'Auditoria realizada em fornecedores com base nos itens P5, P6 e P7 da norma VDA 6.3.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria do processo de manufatura', 'Os fornecedores e subcontratados devem realizar auditorias de processos especiais de manufatura anualmente (CQI-9, CQI-11, CQI-12, CQI-17, CQI-23, CQI-27 e demais aplicáveis). NOTA: Auto avaliação incluindo plano de ação e evidência de implementação de ações corretivas atendem a este requisito.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditoria PPV (Parts Production Verification)', 'Realização da análise crítica do processo confrontando o processo real com o FCPQ');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditorias de primeira parte', 'Auto Avaliação realizada pelo fornecedor');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditorias de processo em camadas (VRS)', 'Devem ser executadas por gerentes operacionais (nenhuma qualificação especial para auditoria exigida, mas o treinamento em VRS requerido) e deve cobrir todas as atividades operacionais (produção e logística). Um cronograma regular deve ser elaborado e a eficiência do VRS deve ser avaliada. Consulte os materiais de treinamento do QIP para a implementação do VRS (01601_13_00154), que detalha os requisitos.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auditorias de segunda parte', 'Avaliações previamente acordadas com o fornecedor. Sempre que solicitado, o fornecedor deverá preencher Check List prévio fornecido pela YAZAKI e enviá-lo para o e-mail supplier.management@br.yazaki.com.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auto QAV', 'Auditorias de primeira parte Honda - Auditoria realizada pelo próprio fornecedor, antes da auditoria oficial realizada pelo cliente final');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auto QSB', 'Auditorias de primeira parte PSA - Auditoria realizada pelo próprio fornecedor, antes da auditoria oficial realizada pelo cliente final');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Auxílios de verificação', 'Todos os documentos, incluindo o manual de fotos / defeitos, ajudam a avaliar se uma peça está ou não em conformidade.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Capacidade Produtiva', 'Verifica a viabilidade da produção, tempo de ciclo, máxima capacidade da produção e nível de habilidade dos operadores');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CAR', 'Corretive Action Request - Documento Electrolux atribuído ao mapeamento de problemas de qualidade.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Carta Descontinuidade', 'Infomativo sobre encerramento de um determinado item ou projeto');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Carta Fim Série', 'Infomativo sobre encerramento de um determinado item ou projeto');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Carta Go', 'Documento Oficial sobre intenção de compras de um determinado item');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Carta Intenção Compras', 'Documento Oficial sobre intenção de compras de um determinado item');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CD1, CD2, CD3, CD4, CD5, CD6, CD7, CD8', 'Prefixos de Pedidos Honda com detalhamento sobre o local de destino. A informação “local de destino” de cada item está disponível na carteira de pedidos que é transmitida via EDI. ');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CEP', 'Controle Estatístico de Processo;');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Certificado Matéria Prima', 'Documento que certifica que os produtos foram produzidos de acordo com os atuais requisitos de especificação da Qualidade e Manufatura');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Checklist Traceability Concept', 'Check list preenchido pelo fornecedor para avaliar os conceitos de rastreabilidade do item.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CKD', 'Completely Knocked Down: coleções de peças necessárias para a montagem de um número finito de veículos em uma  fábrica de montagem no exterior.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Código de Roteamento', 'Informação interna PSA transmitida no DELJIT e a ser transcrita nas suas etiquetas somente. Trata-se de uma marcação temporal que permite a organização física dos paletes nas filas de um cross-dock');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Controle de Lote', 'Para obter rastreabilidade e identificação o fornecedor deve fazer o controle de lote no portal com base no tutorial ANEXO 01 do SQM-4-3');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Combined length and girth / Comprimento e circunferência combinados', 'Medição (circunferência [entre os dois lados mais curtos] + lado mais longo)');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CPI', 'Controle de Produção Inicial - É o registro que faz a rastreabilidade de lotes reparados, inspecionados ou produzidos após mudança de especificação, do processo, da embalagem, etc. que influi diretamente na qualidade da Peça/Componente. *Etiqueta que deve ser colada na caixa');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CPQA', 'Critical Process Quality Assurance ou Garantia da Qualidade em Processo Critico) é avaliar nível de garantia da qualidade de processos especiais, definir contramedidas para atingimento da meta e evitar problemas de qualidade antes do início da produção em volume. Após produção em volume, a atividade será aplicada de forma preventiva ou como forma de avaliação de mudanças de processo.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CPK', 'Índice de capabilidade de um processo');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Crise', 'situação de evento temporário que põe em perigo ou afeta adversamente os negócios regulares, geralmente com possíveis efeitos adversos para os clientes.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Cronograma de Desenvolvimento', 'Tipo de cronograma elaborado por equipe multifuncional atendendo às premissa do projeto para a homologação do processo para Produção em Massa.  (Não exige um formulário padrão).  Devem conter alguns tópicos: Documentos da Qualidade, Ferramental & Moldes, Dispositivos de controle, Preparativos para a produção, Homologação de Sub-Fornecedores, evolução do Nível de peça, Estudo de capabilidade do processo, Teste de validação, Embalagens, Treinamento');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Cross-dock', 'Área frontal de armazenamento, entre fornecedores e supermercados.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('CSR', 'Politica Anticorrupção, Antissuborno, Responsabilidade Social e Confidencialidade da Yazaki');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Cutoff time', 'O tempo de corte padrão para liberação do pedido de transporte é até uma determinada hora do dia anterior à coleta.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Dandori Go', 'Documento que autoriza o fornecedor a dar início nas atividades para o desenvolvimento do item');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('DDP', 'Delivered Duty Paid: Condições do Transporte livre de taxas');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('DDU', 'Delivered Duty UnPaid: Condições Transporte livre de taxas Incoterm de referência para os fluxos não EXW');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Declaração De Minerais De Conflito', 'Minerais de Conflito são minerais explorados no leste da República Democrática do Congo, são eles: Tântalo (Ta), Estanho (Sn), Tungstênio (W) e Ouro (Au). O método de declaração deve seguir a versão mais atual do relatório de Minerais de Conflito disponível em www.comflictfreesourcing.org');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Derroga ou Concessão', 'Alteração, modificação, desvio temporário');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Desenho Mapeado', 'Desenho Técnico com detalhamento de todas as notas como: textos, dimensionais, posicionamentos, linhas, espaçamentos, cores...');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Desvio Temporário', 'Alteração, modificação, desvio temporário');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('DMAIC', 'Definir, Medir, Analisar, Aperfeiçoar e Controlar (Seis Sigma)');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('DMQ', 'Digitron Manual da Qualidade');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Documentação laboratorial qualificada', 'Documentos que certificam a competência de laboratórios para os quais os testes foram terceirizados. A PSA não solicita a apresentação destes documentos, mas eles devem estar à disposição da PSA nas instalações do fornecedor.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Documentos de alteração de engenharia autorizados', 'Todos os documentos que descrevem as mudanças acordadas pelo PSA e pelo SPL levadas em conta para a produção das partes, mas ainda não oficializadas');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('DOJO', 'Centro de Treinamento. Centro de Treinamento da área produtiva');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('ECI', 'Engineering Change Information - Quando uma alteração de engenharia (ECI) for implementada, o fornecedor deve garantir sua implementação de modo a não gerar impactos a qualidade e logística da Toyota, garantindo uma implementação suave');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('ECR', 'Engineering Change Request ou Solicitação de Alteração de Engenharia. Solicitação de Alteração de Desenho da Engenharia');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('EDI', 'Electronic Data Interchange, meio para troca / fluxo de informação eletrônica das previsões, ordens firmes e avisos de expedição, detalhada em referencias, fornecedor e centro de produção.');
INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('EDIFACT', 'Electronic Data Interchange For Administration, Commerce and Transport. Conjuntos de normas e recomendações internacionais para a troca automática de informações comerciais');
