INSERT INTO RQCL_GLOSSARIO(TITULO, DESCRICAO)  VALUES ('Run at Rate', 'Documento requerido para verificar a capacidade produtiva e atendimento. Dados de capacidade de máquina, cálculo de eficiência e produtividade são requeridos e abordados neste documento.');
